-- ------------------------------------------------------------------
-- SCRIPT PARA CRIAÇÃO E POPULAÇÃO DO BANCO DE DADOS
-- UNFAE MED APP - VERSÃO UNIFICADA
-- ------------------------------------------------------------------

-- ETAPA 1: PREPARAÇÃO DO AMBIENTE
SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";
SET FOREIGN_KEY_CHECKS=0;

-- ETAPA 2: CRIAÇÃO DO BANCO DE DADOS E TABELAS
CREATE DATABASE IF NOT EXISTS `unifae_med_app` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE `unifae_med_app`;

-- Estruturas das Tabelas
CREATE TABLE `agenda_disciplinas_vinculadas` (`id_agenda_disciplina` int(11) NOT NULL,`id_evento` int(11) NOT NULL,`id_disciplina` int(11) NOT NULL,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `avaliacoes_preenchidas` (`id_avaliacao_preenchida` int(11) NOT NULL,`id_questionario` int(11) NOT NULL,`id_aluno_avaliado` int(11) NOT NULL,`id_avaliador` int(11) DEFAULT NULL,`id_disciplina` int(11) DEFAULT NULL,`id_turma` int(11) DEFAULT NULL,`id_local_evento` int(11) DEFAULT NULL,`data_realizacao` date NOT NULL,`horario_inicio` time DEFAULT NULL,`horario_fim` time DEFAULT NULL,`feedback_positivo` text DEFAULT NULL,`feedback_melhoria` text DEFAULT NULL,`contrato_aprendizagem` text DEFAULT NULL,`status_avaliacao` enum('RASCUNHO','FINALIZADA','CANCELADA') DEFAULT 'RASCUNHO',`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),`local_realizacao` varchar(255) DEFAULT NULL,`nome_avaliador_nao_usuario` varchar(100) DEFAULT NULL,`tipo_avaliador_nao_usuario` varchar(50) DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `competencias_questionario` (`id_competencia_questionario` int(11) NOT NULL,`id_questionario` int(11) NOT NULL,`nome_competencia` varchar(255) NOT NULL,`tipo_item` enum('escala_numerica','texto_livre','multipla_escolha','checkbox') DEFAULT 'escala_numerica',`descricao_prompt` text DEFAULT NULL,`ordem_exibicao` int(11) DEFAULT 0,`obrigatorio` tinyint(1) DEFAULT 1,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `disciplinas` (`id_disciplina` int(11) NOT NULL,`nome_disciplina` varchar(255) NOT NULL,`codigo_disciplina` varchar(20) NOT NULL,`descricao` text DEFAULT NULL,`carga_horaria` int(11) DEFAULT 0,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),`ativa` bit(1) NOT NULL,`sigla_disciplina` varchar(10) DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `disciplinas_turmas` (`id_disciplina_turma` int(11) NOT NULL,`id_disciplina` int(11) NOT NULL,`id_turma` int(11) NOT NULL,`id_professor` int(11) DEFAULT NULL,`data_vinculacao` timestamp NOT NULL DEFAULT current_timestamp(),`ativo` tinyint(1) DEFAULT 1) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `eventos_agenda` (`id_evento` int(11) NOT NULL,`titulo` varchar(255) NOT NULL,`descricao` text DEFAULT NULL,`data_inicio` datetime NOT NULL,`data_fim` datetime DEFAULT NULL,`id_local_evento` int(11) DEFAULT NULL,`id_disciplina` int(11) DEFAULT NULL,`id_turma` int(11) DEFAULT NULL,`id_responsavel` int(11) DEFAULT NULL,`tipo_evento` enum('AULA','PROVA','SEMINARIO','AVALIACAO','REUNIAO','EVENTO') DEFAULT 'AULA',`status_evento` enum('AGENDADO','EM_ANDAMENTO','CONCLUIDO','CANCELADO') DEFAULT 'AGENDADO',`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `locais_eventos` (`id_local_evento` int(11) NOT NULL,`nome_local` varchar(255) NOT NULL,`descricao` text DEFAULT NULL,`endereco` varchar(500) DEFAULT NULL,`capacidade` int(11) DEFAULT 0,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),`cidade` varchar(100) NOT NULL,`estado` varchar(2) NOT NULL,`tipo_local` varchar(100) NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `log_acoes` (`id_log` int(11) NOT NULL,`id_usuario` int(11) DEFAULT NULL,`acao` varchar(100) NOT NULL,`tabela_afetada` varchar(100) DEFAULT NULL,`id_registro_afetado` int(11) DEFAULT NULL,`dados_anteriores` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`dados_anteriores`)),`dados_novos` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL CHECK (json_valid(`dados_novos`)),`ip_usuario` varchar(45) DEFAULT NULL,`user_agent` text DEFAULT NULL,`data_acao` timestamp NOT NULL DEFAULT current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `participantes_eventos` (`id_participante_evento` int(11) NOT NULL,`id_evento` int(11) NOT NULL,`id_usuario` int(11) NOT NULL,`papel_participante` enum('ORGANIZADOR','PARTICIPANTE','CONVIDADO') DEFAULT 'PARTICIPANTE',`confirmado` tinyint(1) DEFAULT 0,`data_confirmacao` timestamp NULL DEFAULT NULL,`observacoes` text DEFAULT NULL,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `permissoes` (`id_permissao` int(11) NOT NULL,`nome_permissao` varchar(100) NOT NULL,`descricao` text DEFAULT NULL,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),`descricao_permissao` text DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `questionarios` (`id_questionario` int(11) NOT NULL,`nome_modelo` varchar(255) NOT NULL,`descricao` text DEFAULT NULL,`tipo_avaliacao` enum('MINI_CEX','AVALIACAO_360_PROFESSOR','AVALIACAO_360_PARES','AVALIACAO_360_EQUIPE','AVALIACAO_360_PACIENTE') NOT NULL,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `respostas_itens_avaliacao` (`id_resposta_avaliacao` int(11) NOT NULL,`id_avaliacao_preenchida` int(11) NOT NULL,`id_competencia_questionario` int(11) NOT NULL,`resposta_valor_numerico` decimal(4,1) DEFAULT NULL,`resposta_texto` text DEFAULT NULL,`resposta_multipla_escolha` varchar(255) DEFAULT NULL,`nao_avaliado` tinyint(1) DEFAULT 0,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `turmas` (`id_turma` int(11) NOT NULL,`nome_turma` varchar(255) NOT NULL,`ano_letivo` year(4) NOT NULL,`semestre` tinyint(4) NOT NULL CHECK (`semestre` in (1,2)),`data_inicio` date DEFAULT NULL,`data_fim` date DEFAULT NULL,`ativo` tinyint(1) DEFAULT 1,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),`codigo_turma` varchar(50) DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `usuarios` (`id_usuario` int(11) NOT NULL,`nome_completo` varchar(255) NOT NULL,`email` varchar(255) NOT NULL,`senha_hash` varchar(255) NOT NULL,`tipo_usuario` enum('ESTUDANTE','PROFESSOR','COORDENADOR','ADMINISTRADOR') NOT NULL,`matricula_RA` varchar(50) DEFAULT NULL,`telefone` varchar(20) DEFAULT NULL,`foto_perfil_path` varchar(500) DEFAULT NULL,`ativo` tinyint(1) DEFAULT 1,`periodo_atual_aluno` varchar(2) DEFAULT NULL,`observacoes_gerais_aluno` text DEFAULT NULL,`id_permissao` int(11) DEFAULT NULL,`data_criacao` timestamp NOT NULL DEFAULT current_timestamp(),`data_atualizacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `usuarios_permissoes` (`id_usuario_permissao` int(11) NOT NULL,`id_usuario` int(11) NOT NULL,`id_permissao` int(11) NOT NULL,`data_atribuicao` timestamp NOT NULL DEFAULT current_timestamp()) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
CREATE TABLE `usuarios_turmas` (`id_usuario_turma` int(11) NOT NULL,`id_usuario` int(11) NOT NULL,`id_turma` int(11) NOT NULL,`papel` enum('ESTUDANTE','PROFESSOR','MONITOR') NOT NULL,`data_vinculacao` timestamp NOT NULL DEFAULT current_timestamp(),`ativo` tinyint(1) DEFAULT 1) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- --------------------------------------------------------
-- ETAPA 3: INSERÇÃO DOS DADOS
-- --------------------------------------------------------
START TRANSACTION;

