INSERT INTO `avaliacoes_preenchidas` (`id_avaliacao_preenchida`, `id_questionario`, `id_aluno_avaliado`, `id_avaliador`, `id_disciplina`, `id_turma`, `id_local_evento`, `data_realizacao`, `horario_inicio`, `horario_fim`, `feedback_positivo`, `feedback_melhoria`, `status_avaliacao`, `local_realizacao`, `nome_avaliador_nao_usuario`, `tipo_avaliador_nao_usuario`, `contrato_aprendizagem`) VALUES (1, 1, 11, 5, 8, 1, 6, '2025-08-20', '08:00:00', '09:30:00', 'Excelente capacidade de comunicação com o paciente. Demonstrou empatia e profissionalismo.', 'Melhorar a sistematização do exame físico, especialmente na ausculta cardíaca.', 'FINALIZADA', NULL, NULL, NULL, NULL),(2, 1, 12, 6, 8, 1, 6, '2025-08-21', '14:00:00', '15:30:00', 'Boa técnica de anamnese, conseguiu obter informações relevantes do paciente.', 'Trabalhar na organização do tempo durante o atendimento.', 'FINALIZADA', NULL, NULL, NULL, NULL),(3, 1, 13, 5, 8, 1, 7, '2025-08-22', '09:00:00', '10:30:00', 'Demonstrou conhecimento teórico sólido e boa aplicação prática.', 'Desenvolver mais confiança na apresentação dos casos clínicos.', 'FINALIZADA', NULL, NULL, NULL, NULL),(4, 2, 21, 9, 9, 3, 1, '2025-08-23', '10:00:00', '11:00:00', 'Estudante dedicado, sempre pontual e preparado para as atividades.', 'Participar mais ativamente das discussões de caso.', 'FINALIZADA', NULL, NULL, NULL, NULL),(5, 2, 22, 10, 9, 3, 1, '2025-08-24', '15:00:00', '16:00:00', 'Excelente relacionamento interpessoal com pacientes e equipe.', 'Aprimorar conhecimentos em farmacologia clínica.', 'FINALIZADA', NULL, NULL, NULL, NULL),(6, 3, 11, 12, 8, 1, 6, '2025-08-25', '08:30:00', '09:00:00', 'Colega muito colaborativo, sempre disposto a ajudar.', 'Poderia ser mais assertivo nas discussões de grupo.', 'FINALIZADA', NULL, NULL, NULL, NULL),(7, 3, 12, 11, 8, 1, 6, '2025-08-25', '09:00:00', '09:30:00', 'Demonstra muito conhecimento e compartilha bem com o grupo.', 'Às vezes pode ser muito detalhista, perdendo o foco principal.', 'FINALIZADA', NULL, NULL, NULL, NULL),(11, 3, 11, 12, NULL, NULL, NULL, '2025-08-25', '08:30:00', '09:00:00', 'Colega muito colaborativo, sempre disposto a ajudar.', 'Poderia ser mais assertivo nas discussões de grupo.', 'RASCUNHO', 'Ambulatório de Clínica Médica', NULL, NULL, NULL),(12, 3, 12, 11, NULL, NULL, NULL, '2025-08-25', '09:00:00', '09:30:00', 'Demonstra muito conhecimento e compartilha bem com o grupo.', 'Às vezes pode ser muito detalhista, perdendo o foco principal.', 'RASCUNHO', 'Ambulatório de Ginecologia', NULL, NULL, NULL),(13, 3, 11, 12, NULL, NULL, NULL, '2025-08-25', '08:30:00', '09:00:00', 'Colega muito colaborativo, sempre disposto a ajudar.', 'Poderia ser mais assertivo nas discussões de grupo.', 'RASCUNHO', 'Ambulatório de Ginecologia', NULL, NULL, NULL),(14, 2, 22, 10, NULL, NULL, NULL, '2025-08-24', '15:00:00', '16:00:00', 'Excelente relacionamento interpessoal com pacientes e equipe.', 'Aprimorar conhecimentos em farmacologia clínica.', 'RASCUNHO', 'Ambulatório de Clínica Médica', NULL, 'Professor/Preceptor', 'teste'),(15, 3, 13, 14, NULL, NULL, NULL, '2025-09-02', '16:57:00', '19:57:00', 'Preenchimento de teste', 'Preenchimento de teste', 'RASCUNHO', 'Hospital Universitário - Enfermaria A', NULL, NULL, NULL),(19, 5, 12, NULL, NULL, NULL, NULL, '2025-09-01', '16:16:00', '16:16:00', 'A atenção com o paciente.', 'Explicar com mais calma.', 'RASCUNHO', 'Sala de Simulação Clínica', '', 'Paciente', NULL),(23, 4, 11, NULL, NULL, NULL, NULL, '2025-09-02', '22:05:00', '00:05:00', 'Teste de feedback do estudante.', 'Teste de melhoria pelo feedback.', 'RASCUNHO', 'Sala de Simulação Clínica', 'João da Silva', 'Tecnico de Enfermagem', NULL),(24, 4, 12, NULL, NULL, NULL, NULL, '2025-09-02', '00:56:00', '08:00:00', 'Preenchimento de teste para equipes.', 'Preenchimento de teste para equipes a ser melhorado.', 'RASCUNHO', 'Hospital Universitário - UTI', 'Maria de Fátima', 'Psicologia', NULL),(25, 5, 11, NULL, NULL, NULL, NULL, '2025-09-02', '00:00:00', '01:00:00', 'Feedback positivo.', 'Melhorar é preciso.', 'RASCUNHO', 'Sala de Simulação Clínica', 'Paulo da Silva', 'Familiar', NULL),(26, 4, 14, NULL, NULL, NULL, NULL, '2025-09-02', '23:31:00', '02:31:00', 'Teste feedback.', 'Teste feedback melhorias.', 'RASCUNHO', 'Hospital Universitário - Pronto Socorro', 'Cláudia Romano', 'Psicologia', NULL),(27, 2, 18, 6, NULL, NULL, NULL, '2025-09-01', '16:18:00', '21:18:00', 'Desempenho padrão do estudante.', 'Melhorar a atenção com o público em geral.', 'RASCUNHO', 'Hospital Universitário - Centro Cirúrgico', NULL, 'Professor/Preceptor', 'O aprimoramento deve ser de acordo com as novas diretivas do PPC.'),(29, 2, 13, 7, NULL, NULL, NULL, '2025-09-08', '17:23:00', '19:23:00', 'Aluno com bom desempenho.', 'Melhorar a prescrição médica.', 'RASCUNHO', 'Hospital Universitário - Enfermaria A', NULL, 'Professor/Preceptor', 'Alinhar com os professores.'),(30, 3, 14, 13, NULL, NULL, NULL, '2025-09-09', '17:25:00', '22:25:00', 'Atitudes foram boas com o paciente.', 'Falta melhorar a competência técnica.', 'RASCUNHO', 'Ambulatório de Pediatria', NULL, NULL, NULL),(31, 4, 19, NULL, NULL, NULL, NULL, '2025-09-01', '22:26:00', '09:26:00', 'Atendimento foi bom.', 'Precisa melhorar nos quesitos de trabalho em equipe.', 'RASCUNHO', 'UBS Vila Nova', 'Carlos dos Santos', 'Tecnico de Enfermagem', NULL),(34, 1, 20, 8, NULL, NULL, NULL, '2025-08-31', '17:22:00', '19:22:00', 'O aluno foi mediano, tanto no atendimento quanto na prescrição.', 'Sem sugestões.', 'RASCUNHO', 'Hospital Universitário - Enfermaria A', NULL, NULL, 'Sem plano de ação.'),(35, 2, 14, 6, NULL, NULL, NULL, '2025-09-01', '19:18:00', '21:18:00', 'Teste de desempenho do estudante.', 'Não há melhorias sugeridas.', 'RASCUNHO', 'Ambulatório de Clínica Médica', NULL, 'Professor/Preceptor', 'Contato com o coordenador para ajustes de comportamento junto ao paciente.'),(36, 3, 13, 15, NULL, NULL, NULL, '2025-09-01', '18:19:00', '20:19:00', 'Teste de desempenho positivo.', 'Sem observações importantes.', 'RASCUNHO', 'Ambulatório de Ginecologia', NULL, NULL, NULL),(37, 4, 16, NULL, NULL, NULL, NULL, '2025-09-01', '18:21:00', '20:21:00', 'Teste de desempenho de equipe.', 'Teste de melhoria de desempenho.', 'RASCUNHO', 'Laboratório de Anatomia', 'Maria de Fátima', 'Psicologia', NULL),(38, 5, 15, NULL, NULL, NULL, NULL, '2025-09-01', '18:22:00', '19:22:00', 'Teste de atendimento do aluno. Ok.', 'Melhorias no momento de receber o paciente no consultório.', 'RASCUNHO', 'Hospital Universitário - Centro Cirúrgico', 'Paulo Cunha', 'Paciente', NULL),(39, 1, 22, 10, NULL, NULL, NULL, '2025-09-01', '18:44:00', '20:44:00', 'Teste do que foi bem executado. Ok para o aluno.', 'Sem sugestões anotadas.', 'RASCUNHO', 'Laboratório de Histologia', NULL, NULL, 'Plano de aprendizagem padrão.'),(40, 1, 24, 9, NULL, NULL, NULL, '2025-09-01', '22:41:00', '00:41:00', 'Avaliação considerada satisfatória.', 'Melhorar nos quesitos de entrevista médica e exame físico.', 'RASCUNHO', 'Ambulatório de Clínica Médica', NULL, NULL, 'Plano padrão.'),(41, 4, 17, NULL, NULL, NULL, NULL, '2025-09-02', '22:34:00', '00:40:00', 'Avaliação satisfatória em todos os quesitos avaliados.', 'Atenção com o paciente.', 'RASCUNHO', 'Hospital Universitário - Centro Cirúrgico', 'João da Silva', 'Fisioterapia', NULL),(42, 5, 15, NULL, NULL, NULL, NULL, '2025-09-02', '00:36:00', '03:36:00', 'Atitude respeitosa com o paciente.\r\n', 'Comunicação mais clara, diante do diagnóstico.', 'RASCUNHO', 'Ambulatório de Pediatria', 'Maria de Jesus', 'Familiar', NULL);
