-- ETAPA 4: PROCEDIMENTOS, GATILHOS E VIEWS
-- --------------------------------------------------------

DELIMITER $$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_estatisticas_usuario` (IN `usuario_id` INT)   BEGIN
    SELECT 
        u.nome_completo,
        u.tipo_usuario,
        COUNT(CASE WHEN ap.id_aluno_avaliado = usuario_id THEN 1 END) as avaliacoes_recebidas,
        COUNT(CASE WHEN ap.id_avaliador = usuario_id THEN 1 END) as avaliacoes_realizadas,
        AVG(CASE WHEN ap.id_aluno_avaliado = usuario_id THEN ria.resposta_valor_numerico END) as media_recebida,
        COUNT(CASE WHEN ap.id_aluno_avaliado = usuario_id AND ap.status_avaliacao = 'FINALIZADA' THEN 1 END) as finalizadas_recebidas,
        COUNT(CASE WHEN ap.id_avaliador = usuario_id AND ap.status_avaliacao = 'FINALIZADA' THEN 1 END) as finalizadas_realizadas
    FROM usuarios u
    LEFT JOIN avaliacoes_preenchidas ap ON (u.id_usuario = ap.id_aluno_avaliado OR u.id_usuario = ap.id_avaliador)
    LEFT JOIN respostas_itens_avaliacao ria ON ap.id_avaliacao_preenchida = ria.id_avaliacao_preenchida
    WHERE u.id_usuario = usuario_id
    GROUP BY u.id_usuario, u.nome_completo, u.tipo_usuario;
END$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_relatorio_avaliacoes_periodo` (IN `data_inicio` DATE, IN `data_fim` DATE)   BEGIN
    SELECT 
        q.nome_modelo as questionario,
        COUNT(*) as total_avaliacoes,
        COUNT(CASE WHEN ap.status_avaliacao = 'FINALIZADA' THEN 1 END) as finalizadas,
        COUNT(CASE WHEN ap.status_avaliacao = 'RASCUNHO' THEN 1 END) as rascunhos,
        AVG(ria.resposta_valor_numerico) as media_geral,
        MIN(ap.data_realizacao) as primeira_avaliacao,
        MAX(ap.data_realizacao) as ultima_avaliacao
    FROM avaliacoes_preenchidas ap
    JOIN questionarios q ON ap.id_questionario = q.id_questionario
    LEFT JOIN respostas_itens_avaliacao ria ON ap.id_avaliacao_preenchida = ria.id_avaliacao_preenchida
    WHERE ap.data_realizacao BETWEEN data_inicio AND data_fim
    GROUP BY q.id_questionario, q.nome_modelo
    ORDER BY total_avaliacoes DESC;
END$$
CREATE TRIGGER `tr_usuarios_insert` AFTER INSERT ON `usuarios` FOR EACH ROW BEGIN
    INSERT INTO log_acoes (id_usuario, acao, tabela_afetada, id_registro_afetado, dados_novos)
    VALUES (NEW.id_usuario, 'INSERT', 'usuarios', NEW.id_usuario, JSON_OBJECT(
        'nome_completo', NEW.nome_completo,
        'email', NEW.email,
        'tipo_usuario', NEW.tipo_usuario,
        'ativo', NEW.ativo
    ));
END$$
CREATE TRIGGER `tr_usuarios_update` AFTER UPDATE ON `usuarios` FOR EACH ROW BEGIN
    INSERT INTO log_acoes (id_usuario, acao, tabela_afetada, id_registro_afetado, dados_anteriores, dados_novos)
    VALUES (NEW.id_usuario, 'UPDATE', 'usuarios', NEW.id_usuario, 
        JSON_OBJECT(
            'nome_completo', OLD.nome_completo,
            'email', OLD.email,
            'tipo_usuario', OLD.tipo_usuario,
            'ativo', OLD.ativo
        ),
        JSON_OBJECT(
            'nome_completo', NEW.nome_completo,
            'email', NEW.email,
            'tipo_usuario', NEW.tipo_usuario,
            'ativo', NEW.ativo
        )
    );
END$$
DELIMITER ;

CREATE OR REPLACE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vw_avaliacoes_completas`  AS SELECT `ap`.`id_avaliacao_preenchida` AS `id_avaliacao_preenchida`, `ap`.`data_realizacao` AS `data_realizacao`, `ap`.`horario_inicio` AS `horario_inicio`, `ap`.`horario_fim` AS `horario_fim`, `ap`.`status_avaliacao` AS `status_avaliacao`, `q`.`nome_modelo` AS `questionario`, `q`.`tipo_avaliacao` AS `tipo_avaliacao`, `aluno`.`nome_completo` AS `nome_aluno`, `aluno`.`email` AS `email_aluno`, `avaliador`.`nome_completo` AS `nome_avaliador`, `avaliador`.`email` AS `email_avaliador`, `d`.`nome_disciplina` AS `nome_disciplina`, `t`.`nome_turma` AS `nome_turma`, `le`.`nome_local` AS `nome_local`, `ap`.`feedback_positivo` AS `feedback_positivo`, `ap`.`feedback_melhoria` AS `feedback_melhoria`, `ap`.`data_criacao` AS `data_criacao` FROM ((((((`avaliacoes_preenchidas` `ap` join `questionarios` `q` on(`ap`.`id_questionario` = `q`.`id_questionario`)) join `usuarios` `aluno` on(`ap`.`id_aluno_avaliado` = `aluno`.`id_usuario`)) join `usuarios` `avaliador` on(`ap`.`id_avaliador` = `avaliador`.`id_usuario`)) left join `disciplinas` `d` on(`ap`.`id_disciplina` = `d`.`id_disciplina`)) left join `turmas` `t` on(`ap`.`id_turma` = `t`.`id_turma`)) left join `locais_eventos` `le` on(`ap`.`id_local_evento` = `le`.`id_local_evento`));
CREATE OR REPLACE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vw_estatisticas_avaliacoes`  AS SELECT `u`.`id_usuario` AS `id_usuario`, `u`.`nome_completo` AS `nome_completo`, `u`.`tipo_usuario` AS `tipo_usuario`, count(case when `ap`.`id_aluno_avaliado` = `u`.`id_usuario` then 1 end) AS `total_como_avaliado`, count(case when `ap`.`id_avaliador` = `u`.`id_usuario` then 1 end) AS `total_como_avaliador`, count(case when `ap`.`id_aluno_avaliado` = `u`.`id_usuario` and `ap`.`status_avaliacao` = 'FINALIZADA' then 1 end) AS `finalizadas_como_avaliado`, count(case when `ap`.`id_avaliador` = `u`.`id_usuario` and `ap`.`status_avaliacao` = 'FINALIZADA' then 1 end) AS `finalizadas_como_avaliador`, avg(case when `ria`.`id_avaliacao_preenchida` in (select `avaliacoes_preenchidas`.`id_avaliacao_preenchida` from `avaliacoes_preenchidas` where `avaliacoes_preenchidas`.`id_aluno_avaliado` = `u`.`id_usuario`) then `ria`.`resposta_valor_numerico` end) AS `media_notas_recebidas` FROM ((`usuarios` `u` left join `avaliacoes_preenchidas` `ap` on(`u`.`id_usuario` = `ap`.`id_aluno_avaliado` or `u`.`id_usuario` = `ap`.`id_avaliador`)) left join `respostas_itens_avaliacao` `ria` on(`ap`.`id_avaliacao_preenchida` = `ria`.`id_avaliacao_preenchida`)) WHERE `u`.`ativo` = 1 GROUP BY `u`.`id_usuario`, `u`.`nome_completo`, `u`.`tipo_usuario`;
CREATE OR REPLACE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vw_usuarios_permissoes`  AS SELECT `u`.`id_usuario` AS `id_usuario`, `u`.`nome_completo` AS `nome_completo`, `u`.`email` AS `email`, `u`.`tipo_usuario` AS `tipo_usuario`, `u`.`ativo` AS `usuario_ativo`, group_concat(`p`.`nome_permissao` separator ', ') AS `permissoes` FROM ((`usuarios` `u` left join `usuarios_permissoes` `up` on(`u`.`id_usuario` = `up`.`id_usuario`)) left join `permissoes` `p` on(`up`.`id_permissao` = `p`.`id_permissao` and `p`.`ativo` = 1)) WHERE `u`.`ativo` = 1 GROUP BY `u`.`id_usuario`, `u`.`nome_completo`, `u`.`email`, `u`.`tipo_usuario`, `u`.`ativo`;

